import{_ as n,c as r,e as o,a as l,b as a,d as e,o as t,r as i}from"./app.59268308.js";const F=JSON.parse('{"title":"Navidrome","description":"","frontmatter":{"title":"Navidrome","lang":"es-ES"},"headers":[{"level":2,"title":"El docker-compose.yaml","slug":"el-docker-compose-yaml","link":"#el-docker-compose-yaml","children":[]},{"level":2,"title":"Variables de entorno","slug":"variables-de-entorno","link":"#variables-de-entorno","children":[]},{"level":2,"title":"Configuraci\xF3n de Nginx","slug":"configuracion-de-nginx","link":"#configuracion-de-nginx","children":[]},{"level":2,"title":"Integraci\xF3n con Authentik","slug":"integracion-con-authentik","link":"#integracion-con-authentik","children":[]},{"level":2,"title":"Descargando, etiquetando y subiendo m\xFAsica","slug":"descargando-etiquetando-y-subiendo-musica","link":"#descargando-etiquetando-y-subiendo-musica","children":[{"level":3,"title":"Descarga","slug":"descarga","link":"#descarga","children":[]},{"level":3,"title":"Etiquetado","slug":"etiquetado","link":"#etiquetado","children":[]},{"level":3,"title":"Subida","slug":"subida","link":"#subida","children":[]}]},{"level":2,"title":"Integraciones externas","slug":"integraciones-externas","link":"#integraciones-externas","children":[]},{"level":2,"title":"Clientes para tus dispositivos","slug":"clientes-para-tus-dispositivos","link":"#clientes-para-tus-dispositivos","children":[{"level":3,"title":"iOS","slug":"ios","link":"#ios","children":[]},{"level":3,"title":"Android","slug":"android","link":"#android","children":[]}]}],"relativePath":"servicios/navidrome.md","lastUpdated":1664399954000}'),c={name:"servicios/navidrome.md"},p=l(`<h1 id="navidrome-streaming-de-musica" tabindex="-1">Navidrome - Streaming de m\xFAsica <a class="header-anchor" href="#navidrome-streaming-de-musica" aria-hidden="true">#</a></h1><p><a href="https://navidrome.org" target="_blank" rel="noreferrer">Navidrome</a> es un servicio basado en <a href="http://www.subsonic.org/pages/index.jsp" target="_blank" rel="noreferrer">Subsonic</a> que nos permite crear un servicio de streaming de m\xFAsica como Spotify o YouTube Music. De por s\xED, Subsonic es una plataforma dise\xF1ada para el streaming de tanto contenido de v\xEDdeo como de audio, pero Navidrome est\xE1 dise\xF1ada espec\xEDficamente para solo audio, dando mejor soporte para este pero manteniendo la compatibilidad con otros servicios que utilizan la <a href="http://www.subsonic.org/pages/api.jsp" target="_blank" rel="noreferrer">API de Subsonic</a>.</p><details class="details custom-block"><summary>\xBFQu\xE9 es una API? \xBFY para que me sirve que Navidrome use la de Subsonic?</summary><p>Una API (Application Programming Interface) es un est\xE1ndar de comunicaci\xF3n con un programa, es decir, una serie de reglas y formas que hace p\xFAblicas un servicio para poder interactuar con \xE9l program\xE1ticamente desde otro. En este caso, Subsonic define una API para interactuar con los servidores de m\xFAsica que la implementen.</p><p>Que Navidrome implemente esta API est\xE1ndar de Subsonic permite utilizar todas las aplicaciones y servicios compatibles con la API de Subsonic con nuestra instancia de Navidrome. La principal utilidad de esto es poder escuchar nuestra m\xFAsica desde una app nativa en nuestros dispositivos (como se explica <a href="#clientes-para-tus-dispositivos">m\xE1s adelante</a>)</p></details><h2 id="el-docker-compose-yaml" tabindex="-1">El <code>docker-compose.yaml</code> <a class="header-anchor" href="#el-docker-compose-yaml" aria-hidden="true">#</a></h2><p>Esto es el contenido a a\xF1adir al <code>docker-compose.yaml</code> para habilitar Navidrome:</p><div class="language-yaml"><button class="copy"></button><span class="lang">yaml</span><pre><code><span class="line"><span style="color:#F07178;">services</span><span style="color:#89DDFF;">:</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#F07178;">navidrome</span><span style="color:#89DDFF;">:</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F07178;">image</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">deluan/navidrome:latest</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F07178;">user</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">\${NAVIDROME_UID}:\${NAVIDROME_GID}</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F07178;">restart</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">unless-stopped</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F07178;">volumes</span><span style="color:#89DDFF;">:</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">./navidrome-music:/music:ro</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">./navidrome-data:/data:rw</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F07178;">ports</span><span style="color:#89DDFF;">:</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">4533:4533</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F07178;">environment</span><span style="color:#89DDFF;">:</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ND_LOGLEVEL=info</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ND_CONFIGFILE=/data/navidrome.toml</span></span>
<span class="line"></span></code></pre></div><div class="warning custom-block"><p class="custom-block-title">RECUERDA CAMBIAR LOS PUERTOS</p><p>Simple recordatorio de que todos los puertos aqu\xED mostrados son ilustrativos, y cuando vayas a montar este servicio, es recomendable cambiar todas las asociaciones de puertos que aparecen en esta gu\xEDa.</p><p>Para generar un puerto aleatorio, puedes utilizar por ejemplo <a href="https://it-tools.tech/random-port-generator" target="_blank" rel="noreferrer">esta herramienta online</a> (\xA1aseg\xFArate de no usar 2 veces el mismo puerto!).</p></div><p>Por comodidad, la mayor\xEDa de las opciones de configuraci\xF3n las pondremos en el archivo de configuraci\xF3n <code>navidrome.toml</code>, cuya ubicaci\xF3n se indica con la configuraci\xF3n <code>ND_CONFIGFILE</code> en referencia al sistema de archivos del contenedor.</p><p>Navidrome requiere que identifiques con user al propietario de las carpetas a las que est\xE1n asociados los vol\xFAmenes con la opci\xF3n <code>user</code> (la alternativa es ejecutarlo como <em>root</em>, cosa que est\xE1 <a href="https://www.navidrome.org/docs/usage/security/#permissions" target="_blank" rel="noreferrer">\xE1ltamente desaconsejada por los propios autores</a>).</p><p>Este contenedor tiene dos vol\xFAmenes disponibles para asociar:</p><ul><li><p><code>/music:ro</code>: En esta carpeta es donde Navidrome escanear\xE1 por archivos de m\xFAsica, as\xED que aqu\xED es donde tendr\xE1s que subir toda tu m\xFAsica etiquetada apropiadamente (m\xE1s info de c\xF3mo etiquetarla <a href="#descargando-etiquetando-y-subiendo-m%C3%BAsica">m\xE1s adelante</a>). Dado que Navidrome solo necesita escanear tu m\xFAsica, no necesita modificarla para nada, y por tanto podemos (y debemos) marcar el volumen como <em>read-only</em> a\xF1adiendo el <code>:ro</code> al final.</p></li><li><p><code>/data:rw</code>: aqu\xED es donde Navidrome almacena toda su base de datos y el resto de datos que pueda necesitar, y en nuestro caso tambi\xE9n el archivo de configuraci\xF3n <code>navidrome.toml</code>.Dado que Navidrome solo necesita modificar todos los archivos de esta carpeta, le a\xF1adimos <code>:rw</code> a este volumen al final para indicar que tiene permisos de <em>read-write</em></p></li></ul><h2 id="variables-de-entorno" tabindex="-1">Variables de entorno <a class="header-anchor" href="#variables-de-entorno" aria-hidden="true">#</a></h2><p>Esto es el contenido a a\xF1adir al <code>.env</code> para pasar a docker:</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">NAVIDROME_UID=1000</span></span>
<span class="line"><span style="color:#A6ACCD;">NAVIDROME_GID=1000</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>Aqu\xED tenemos que cambiar <code>NAVIDROME_UID</code> y <code>NAVIDROME_GID</code> por el ID de usuario y el ID del grupo, respectivamente, al que pertenezcan las carpetas en nuestro sistema que hayamos asociado a los vol\xFAmenes. Para hacerlo m\xE1s f\xE1cil y simple, recomendamos poner el usuario que usamos para docker (nuestro <code>dockeruser</code>) como propietario de los archivos, tanto como propietario individual como de grupo. Suponiendo que ya hayas creado las carpetas que has asociado a los vol\xFAmenes, puedes ejecutar los siguientes comandos para ajustar los permisos (cambiando <code>dockeruser</code> por el nombre de usuario que hayas decidido para usar docker):</p><div class="language-sh"><button class="copy"></button><span class="lang">sh</span><pre><code><span class="line"><span style="color:#676E95;"># Carpeta para el volumen /music</span></span>
<span class="line"><span style="color:#A6ACCD;">$ sudo chown dockeruser /path/to/your/navidrome-music</span></span>
<span class="line"><span style="color:#A6ACCD;">$ sudo chgrp dockeruser /path/to/your/navidrome-music</span></span>
<span class="line"><span style="color:#676E95;"># Carpeta para el volumen /data</span></span>
<span class="line"><span style="color:#A6ACCD;">$ sudo chown dockeruser /path/to/your/navidrome-data</span></span>
<span class="line"><span style="color:#A6ACCD;">$ sudo chgrp dockeruser /path/to/your/navidrome-data</span></span>
<span class="line"></span></code></pre></div><p>Y ahora, para saber que ID de usuario y grupo tiene nuestro <code>dockeruser</code> y tenemos que poner en el <code>.env</code>, ejecutamos lo siguiente:</p><div class="language-sh"><button class="copy"></button><span class="lang">sh</span><pre><code><span class="line"><span style="color:#A6ACCD;">$ id dockeruser</span></span>
<span class="line"></span></code></pre></div><h2 id="configuracion-de-nginx" tabindex="-1">Configuraci\xF3n de Nginx <a class="header-anchor" href="#configuracion-de-nginx" aria-hidden="true">#</a></h2><p>La configuraci\xF3n de Nginx para Navidrome es la standard de reverse-proxy que usamos en otros servicios. Copia y pega el contenido en el archivo <code>/etc/nginx/conf.d/music.server.net.conf</code> (donde <code>server.net</code> es tu dominio y <code>music</code> el subdominio, que puedes cambiar a tu gusto):</p><div class="language-nginx"><button class="copy"></button><span class="lang">nginx</span><pre><code><span class="line"><span style="color:#C792EA;">server</span><span style="color:#A6ACCD;"> {</span></span>
<span class="line"><span style="color:#A6ACCD;">   </span><span style="color:#89DDFF;"> server_name </span><span style="color:#A6ACCD;">music.server.net</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#C792EA;">location</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">/ </span><span style="color:#A6ACCD;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">       </span><span style="color:#89DDFF;"> proxy_pass </span><span style="color:#A6ACCD;">http://127.0.0.1:31401</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">       </span><span style="color:#89DDFF;"> proxy_set_header </span><span style="color:#A6ACCD;">       Host </span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">host</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">       </span><span style="color:#89DDFF;"> proxy_set_header </span><span style="color:#A6ACCD;">       X-Real-IP </span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">remote_addr</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">       </span><span style="color:#89DDFF;"> proxy_set_header </span><span style="color:#A6ACCD;">       X-Forwarded-For </span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">remote_addr</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">       </span><span style="color:#89DDFF;"> proxy_set_header </span><span style="color:#A6ACCD;">       Upgrade </span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">http_upgrade</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">       </span><span style="color:#89DDFF;"> proxy_set_header </span><span style="color:#A6ACCD;">       Connection </span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">http_connection</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">    }</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"></span></code></pre></div><p>Y ahora ejecutamos <em>certbot</em> para crear y configurar el dominio con HTTPS:</p><div class="language-sh"><button class="copy"></button><span class="lang">sh</span><pre><code><span class="line"><span style="color:#A6ACCD;">$ certbot --nginx -d music.server.net</span></span>
<span class="line"></span></code></pre></div><div class="tip custom-block"><p class="custom-block-title">RECORDATORIOS</p><p>Para que <em>certbot</em> genere correctamente los certificados tienes que haber configurado previamente el DNS del subdominio (<a href="/server/equipo/router-dominio.html#actualizando-la-ip-publica-en-el-domino-automaticamente">m\xE1s info</a>). No te olvides de recargar (<code>reload</code>) Nginx tras guardar el archivo.</p></div><h2 id="integracion-con-authentik" tabindex="-1">Integraci\xF3n con Authentik <a class="header-anchor" href="#integracion-con-authentik" aria-hidden="true">#</a></h2><p>TODO</p><h2 id="descargando-etiquetando-y-subiendo-musica" tabindex="-1">Descargando, etiquetando y subiendo m\xFAsica <a class="header-anchor" href="#descargando-etiquetando-y-subiendo-musica" aria-hidden="true">#</a></h2><p>Para poder escuchar m\xFAsica a trav\xE9s de Navidrome, tenemos que proporcion\xE1rsela nosotros mismos en forma de archivos de audio. Aqu\xED se muestra una posible forma de realizar este proceso, aunque lo puedes hacer de una infinidad de formas. Al fin y al cabo se trata de que los archivos de audio de la m\xFAsica que quieras acaben el el volumen de m\xFAsica de Navidrome que hemos asignado previamente.</p><p>Antes de comenzar, crea una carpeta en tu ordenador para descargar la m\xFAsica y sincronizarla (si vas a seguir estos pasos, si prefieres hacerlo de otra forma eres libre de ello)</p><h3 id="descarga" tabindex="-1">Descarga <a class="header-anchor" href="#descarga" aria-hidden="true">#</a></h3><p>Para descargar la m\xFAsica, en un principio ten\xEDa pensado utilizar el conocido <a href="https://github.com/ytdl-org/youtube-dl" target="_blank" rel="noreferrer">youtube-dl</a>, pero posteriormente cambi\xE9 a utilizar un fork de este, llamado <a href="https://github.com/yt-dlp/yt-dlp" target="_blank" rel="noreferrer">yt-dlp</a>. El funcionamiento es pr\xE1cticamente igual (a efectos de nuestros comandos, igual). En mi caso, he descargado \xE1lbum por \xE1lbum desde YouTube Music, as\xED que us\xE9 la funcionalidad de descargar playlists. El comando que he usado para descargar las playlists es el siguiente:</p><div class="language-sh"><button class="copy"></button><span class="lang">sh</span><pre><code><span class="line"><span style="color:#676E95;"># Sustituye $url por la URL a la playlist/album a descargar</span></span>
<span class="line"><span style="color:#A6ACCD;">$ yt-dlp --extract-audio --audio -format m4a --output </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">%(playlist_title)s/%(playlist_index)s - %(title)s.%(ext)s</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">url</span></span>
<span class="line"></span></code></pre></div><p>Este comando hace b\xE1sicamente lo siguiente: descarga y extrae el audio de todos los items de la playlist y los guarda en formato <em>m4a</em>.</p><div class="warning custom-block"><p class="custom-block-title">IMPORTANTE COMPROBACI\xD3N</p><p>El formato de archivo <em>m4a</em> proporciona en general mejor calidad que el <em>mp3</em>, pero Navidrome no soporta todos los encodings posibles de este archivo. Aseg\xFArate de que los archivos que hemos descargado tengan codificaci\xF3n/encoding <strong>AAC</strong>, que lo puedes comprobar con el siguiente comando:</p><div class="language-sh"><button class="copy"></button><span class="lang">sh</span><pre><code><span class="line"><span style="color:#A6ACCD;">$ ffmpeg -i playlist_title/audio_file.m4a</span></span>
<span class="line"></span></code></pre></div><p>Deber\xEDas de poder ver una l\xEDnea que te indique que el archivo contiene un stream de audio con codificaci\xF3n AAC, parecido a esto:</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">Stream #0:0(eng): Audio: aac (LC) (mp4a / 0x6134706D), 48000 Hz, stereo, fltp, 207 kb/s (default)</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div></div><p>Para automatizar el proceso, puedes copiar y pegar este c\xF3digo en un archivo ejecutable por bash:</p><div class="language-sh"><button class="copy"></button><span class="lang">sh</span><pre><code><span class="line"><span style="color:#89DDFF;">for</span><span style="color:#A6ACCD;"> url </span><span style="color:#89DDFF;">in</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">@</span><span style="color:#89DDFF;">;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">do</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#82AAFF;">echo</span><span style="color:#A6ACCD;"> -e </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">\\x1b[1;34m**************************************************\\n* STARTING</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">url </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">\\n**************************************************\\x1b[0m</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">  yt-dlp --extract-audio --audio-format m4a --output </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">%(playlist_title)s/%(playlist_index)s - %(title)s.%(ext)s</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">url</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#82AAFF;">echo</span><span style="color:#A6ACCD;"> -e </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">\\x1b[1;32m**************************************************\\n* DOWNLOADED</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">url </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">\\n**************************************************\\x1b[0m</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#89DDFF;">done</span></span>
<span class="line"></span></code></pre></div><p>Este script te permite b\xE1sicamente pasar varias URLs de playlists como par\xE1metros, tal que as\xED:</p><div class="language-sh"><button class="copy"></button><span class="lang">sh</span><pre><code><span class="line"><span style="color:#A6ACCD;">$ ./download_script.sh https://url.to/playlist1 https://url.to/playlist2 https://url.to/playlist3</span></span>
<span class="line"></span></code></pre></div><h3 id="etiquetado" tabindex="-1">Etiquetado <a class="header-anchor" href="#etiquetado" aria-hidden="true">#</a></h3><p>Para etiquetar la m\xFAsica utilizaremos <a href="https://picard.musicbrainz.org/" target="_blank" rel="noreferrer">MusicBrainz&#39;s Picard</a>. Al principio puede que resulte un poco confusa la interfaz y c\xF3mo trabajar con ella, pero en cuanto te acostumbras es una gozada. La ventaja de esta aplicaci\xF3n es que se conecta a la <a href="https://musicbrainz.org/doc/MusicBrainz_Database" target="_blank" rel="noreferrer">base de datos de MusicBrainz</a>, la cual contiene una infinidad de registros de \xE1lbumes, artistas y canciones, con un mont\xF3n de etiqueta y datos de ellos. De hecho, los propios creadores de Navidrome <a href="https://www.navidrome.org/docs/faq/#how-can-i-edit-my-music-metadata-id3-tags-how-can-i-renamemove-my-files" target="_blank" rel="noreferrer">recomiendan este editor</a>.</p><p>Para etiquetar un album, lo primero que hacemos es darle a <em>Add Folder</em> en Picard y seleccionamos la carpeta de ese album, lo que har\xE1 que aparezcan en el panel de la izquierda, bajo <em>Unclustered files</em>, todos los archivos de audio que hubiese en ese album. Ahora los seleccionamos y le damos a <em>Cluster</em>, lo que los agrupar\xE1 bajo una &quot;carpeta virtual&quot; en <em>Clustered files</em>. Primero vamos a probar suerte, le damos a <em>Lookup</em> seleccionando esta &quot;carpeta virtual&quot; que se a creado, y Picard deber\xEDa de dar con el album correcto. Si no da con el album correcto (sale uno que no es o no sale ninguno), le podemos dar a <em>Lookup in Browser</em>, que abrir\xE1 en nuestro navegador una p\xE1gina de b\xFAsqueda para la base de datos de MusicBrainz. Cuando hayamos encontrado el album que buscamos, le damos a el bot\xF3n/ilustraci\xF3n de tagger a la derecha del todo, y volvemos a Picard. Desde aqu\xED arrastramos la &quot;carpeta virtual&quot; hasta el album a la derecha, ajustamos lo que queramos cambiar (<strong>importante revisar la etiqueta <em>length</em>, que cambia muchas veces</strong>) y le damos a <em>Save</em> teniendo seleccionado el album, y a <em>Remove</em> para quitarlo de Picard.</p><p>Si no te ha quedado claro (tampoco espero que sea el mejor tutorial del mundo sinceramente), la documentaci\xF3n de Navidrome sugiere <a href="https://www.thedreaming.org/2020/11/22/musicbrainz-picard/" target="_blank" rel="noreferrer">este tutorial</a>, y si sigue sin quedarte claro, internet es tu amigo.</p><h3 id="subida" tabindex="-1">Subida <a class="header-anchor" href="#subida" aria-hidden="true">#</a></h3><p>Para subir la m\xFAsica hemos utilizado <a href="https://syncthing.net/" target="_blank" rel="noreferrer">Syncthing</a>, un software de sincronizaci\xF3n de archivos. Este software te permite sincronizar carpetas entre m\xFAltiples dispositivos. Para instalarlo, utiliza el m\xE9todo que prefieras de entre los que se encuentran en la <a href="https://syncthing.net/downloads/" target="_blank" rel="noreferrer">documentaci\xF3n</a>. Tras haberlo instalado, vamos a configurarlo.</p><p>El primer paso es ejecutarlo en nuestro ordenador en el que hemos estado descargando la m\xFAsica a la vez que en el servidor remoto (desde una sesi\xF3n de SSH), simplemente ejcut\xE1ndolo desde la terminal como sigue (o de otra forma si vuestra instalaci\xF3n lo requiere):</p><div class="language-sh"><button class="copy"></button><span class="lang">sh</span><pre><code><span class="line"><span style="color:#676E95;"># Run this both on your local and remote machine</span></span>
<span class="line"><span style="color:#A6ACCD;">$ syncthing</span></span>
<span class="line"></span></code></pre></div><p>Para poder configurar Syncthing en el servidor remoto, tenemos que acceder a la interfaz web, lo cual haremos con el uso de las capacidades de port-forwarding de SSH con el siguiente comando (en vuestra m\xE1quina local):</p><div class="language-sh"><button class="copy"></button><span class="lang">sh</span><pre><code><span class="line"><span style="color:#A6ACCD;">$ ssh -L 8385:127.0.0.1:8384 -N admin@server.net -p 29945</span></span>
<span class="line"></span></code></pre></div><p>Hecho esto, debes de entrar a ambas interfaces, y tras emparejarlas, tienes que configurar una carpeta a compartir en cada una. En tu ordenador, config\xFArala para solo enviar, mientras que en tu servidor debes de configurarla para solo recibir. De esta forma, mientras tengas <code>syncthing</code> ejecut\xE1ndose en ambos, se enviar\xE1n los todos los archivos de tu ordenador al servidor.</p><h2 id="integraciones-externas" tabindex="-1">Integraciones externas <a class="header-anchor" href="#integraciones-externas" aria-hidden="true">#</a></h2><h2 id="clientes-para-tus-dispositivos" tabindex="-1">Clientes para tus dispositivos <a class="header-anchor" href="#clientes-para-tus-dispositivos" aria-hidden="true">#</a></h2><p>Estas son recomendaciones personales de clientes, que he encontrado con una interfaz f\xE1cil de usar y bonita, a parte de con todas las funcionalidades que puedes esperar. Hay una cantidad pr\xE1cticamente infinita de ellos, especialmente en Android, en iOS todo el desarrollo se ve m\xE1s capado por los altos costes de distribuci\xF3n; si no te gusta alguno de estos, busca algo que te vaya mejor.</p><details class="details custom-block"><summary>Y para t\xFA port\xE1til...</summary><p>Usa la propia interfaz web</p><details class="details custom-block"><summary>Si realmente lo necesitas...</summary><p>Internet es tu aliado</p></details></details><h3 id="ios" tabindex="-1">iOS <a class="header-anchor" href="#ios" aria-hidden="true">#</a></h3>`,54),d=a("p",null,[e("En iOS no hay tanto para elegir como en otras plataformas, debido principalmente al alto coste de desarrollo que tiene la plataforma. Lo mejor que he podido encontrar es "),a("code",null,"SubStreamer"),e(". No me malinterpret\xE9is, es incre\xEDble. Tiene un mont\xF3n de funcionalidades y la interfaz es bastante c\xF3moda y bonita. Desventaja: es c\xF3digo cerrado, pero no vas a encontrar mucho c\xF3digo abierto en iOS en cualquier caso, as\xED que es lo que hay.")],-1),u=a("h3",{id:"android",tabindex:"-1"},[e("Android "),a("a",{class:"header-anchor",href:"#android","aria-hidden":"true"},"#")],-1),m=a("p",null,[e("En el campo de Android, hay una amplia cantidad de reproductores de c\xF3digo abierto, que son los que l\xF3gicamente deber\xEDamos de instalar si hemos pasado por instalar nuestro propio servicio de streaming musical. Aunque hay reproductores con probablemente mayor velocidad y funcionalidades, personalmente me he decantado por "),a("code",null,"Subtracks"),e(", que tiene una muy c\xF3moda interfaz (que recuerda un poco a Spotify), lo cual es dif\xEDcil encontrar, sin perder funcionalidades.")],-1),y=a("div",{class:"warning custom-block"},[a("p",{class:"custom-block-title"},"OJO, VERSI\xD3N 2.0 EN CAMINO"),a("p",null,"Ahora mismo igual te pueden faltar un par de cosas en esta app: gesti\xF3n de la cola, gesti\xF3n de playlists, reproducir todo de un artista, etc. Esto pronto ser\xE1 solventado, ya que el desarrollador est\xE1 trabajando en la versi\xF3n 2.0, pero tarda un poco dado que cambia completamente el lenguaje en la que esta est\xE1 programada. Si quieres saber m\xE1s, mira (este issue en el repo)[]")],-1),h=a("p",null,[a("code",null,"SubStramer"),e(" tambi\xE9n est\xE1 disponible en la Play Store, aunque funciona m\xE1s lento que en iOS y por alguna raz\xF3n conserva el estilo iOS. Ahora mismo tiene un mayor set de funcionalidades, y una especialmente chula son las radios y playlists auto-generadas. Por tanto, si realmente es algo que necesitas s\xED o s\xED, usa esa app mientras "),a("code",null,"Subtracks 2.0"),e(" se encuentra en desarrollo.")],-1);function g(D,b,A,v,C,f){const s=i("AppCard");return t(),r("div",null,[p,o(s,{class:"margin-top",name:"SubStreamer",image:"../images/substreamer.png",summary:"SubStreamer is a completely free app to use with any subsonic compatible music server (subsonic, airsonic, madsonic, navidrome, ampache etc). Your music when you want it, where you want it, online or offline.",links:[{store:"app-store",url:"https://apps.apple.com/us/app/substreamer/id1012991665"}]},null,8,["links"]),d,u,o(s,{class:"margin-top",name:"Subtracks",image:"../images/subtracks.png",summary:"Subtracks is an Android open source music streaming app for Subsonic-compatible servers (Subsonic, Navidrome, Airsonic, and more). It's designed to give you clean and convenient access to your music in the style of modern media players.",links:[{store:"f-droid",url:"https://f-droid.org/en/packages/com.subtracks/"}]},null,8,["links"]),m,y,h])}const _=n(c,[["render",g]]);export{F as __pageData,_ as default};
